import  React, {Component} from 'react';
import ReactDOM from 'react-dom';
import { Route, Link } from 'react-router-dom';
import {withRouter} from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import Home from '../home/home';
import Projects from '../projects/projects';
import Contact from '../contact/contact';
import './about.css';

class About extends Component {
    render() {
      return (
        <div className='Projects'>
            <header className='projectsHeader'>
                <h1>Portfolio</h1>
            </header>
            <main>
            <h2>A Hard Day's Night</h2>
      <p>A fairly standard multiple choice quiz, A Hard Day’s Quiz uses a form element and radio buttons to present users with ten multiple choice questions on the Beatles. After selecting an answer, users receive immediate feedback on if they answered correctly.
        If the answer is incorrect, the correct answer is shown. Users’ score and the number of the question that they are on are shown and updated with each question. After the last question, users are presented with their final score and with a restart
        button that allows them to start the quiz from the beginning.</p>
      {/* <img id="HardDaysImage" src="https://user-images.githubusercontent.com/46304321/53762550-69a5c980-3e96-11e9-9ad6-d8aeccf45265.png" alt="screenhot of A Hard Day's Quiz app">
      <p><b>Technologies Used:</b> HTML, CSS, JavaScript, JQuery</p>
      <p> <a href="https://reperez120.github.io/Beatles-Quiz" target="_blank"> Live Version</a> </p>
      <p> <a href="https://github.com/reperez120/Beatles-Quiz" target="_blank"> Repo Version</a> </p> */}

            </main>
        </div>
      );
    } 
}

export default About;
